// use selector use for state track 

import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";

const Cart = () => {
 
 const {cart} = useSelector((state) => state);
  return (
    <div>
      <div>
        
      </div>
      {
        cart.length > 0 ?
        (<div>
          {
            cart.map((index,item) => {
              return <CardItem key={item.id} item={item} itemIndex={index}/>
            })
          }

        </div>) :
        (<div>
          <h1>Cart Empty</h1>
          <NavLink to={"/"}>
            <button>
              Shop Now
            </button>
          </NavLink>
        </div>)
     }

    </div>






  )
};

export default Cart;
