import {FcDeleteDatabase} from "react-icons/fc"
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast"


const CartItem = ({item,itemIndex}) => {
    const dispatch = useDispatch();

    const removeFromCart = () => {
      dispatch(remove(item.id));
      toast.success("Item Removed")
    }

  
  return (
    <div className="flex ">

     <div className="flex gap-4 m-3 mr-7 ]">

      <div className="h-[180px] flex items-center my-auto max-w-[100px]">
        <img src={item.image} alt="" className='h-full w-full'/>
      </div>
      <div className="flex flex-col gap-7">
        <h1 className='text-gray-700 font-semibold text-lg text-left w-40'>{item.title}</h1>
        <h1 className='w-40 text-gray-400 font-normal text-[10px] text-left'>{item.description.split(" ").slice(0,10).join(" ") + "..."}</h1>
        <div className="flex flex-row justify-between border-b-2">
        <p className="text-green-600 font-semibold text-left w-40">${item.price}</p>

          <div 
          onClick={removeFromCart}
          className="text-lg hover:scale-110 transition duration-300 ease-in">
            <FcDeleteDatabase/>
          </div>
        </div>
      </div>

     </div>
      
  </div>


  );
}


export default CartItem;
