// use selector use for state track 

import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import CardItem from "../components/CartItem"

const Cart = () => {
 
 const {cart} = useSelector((state) => state);
 const[totalAmount,setTotalAmount] = useState(0)
 
 useEffect( () =>{
  setTotalAmount( cart.reduce( (acc,curr) => acc + curr.price,0));
 },[cart])

  return (
    <div className="flex justify-center">
      {
        cart.length > 0 ?
        (<div className="flex flex-row max-w-[1080px] p-2 mx-auto min-h-[80vh]">
         
          <div>
            {
              cart.map((item,index) => {
                return <CardItem key={item.id} item={item} itemIndex={index} />
              })
            }
          </div>


          <div className="flex flex-col justify-between ml-[20px]">
            <div className="flex flex-col mx-auto">
              <div className='text-green-600 font-semibold text-lg w-40 mt-1'>Your Cart</div>
              <div className='text-green-600 font-semibold text-3xl w-40 '>Summary</div>
              <p className="text-gray-700 font-semibold text-lg text-left w-40 mt-5">
                <span>Total Items:{cart.length}</span>
              </p>
            </div>

            <div className="flex flex-col mb-0">
              <div className="flex">
              <p className="text-gray-700 font-semibold text-lg text-left">Total Amount:</p>
              <p className="text-gray-700 font-bold text-lg text-left">${totalAmount}</p> 
              </div>
             
              <button className='bg-white text-green-600 font-semibold text-xl rounded-md mt-2
              hover:scale-110  hover:bg-green-600 hover:text-white'>
              CheckOut Now
            </button>
            </div>
            
          </div>
 

 
          </div>) :
        (<div className="flex gap-3 h-screen justify-center items-center">
          <h1 className="text-gray-700 font-semibold text-lg text-left ">Cart Empty</h1>
          <NavLink to={"/"}>
            <button className='hover:scale-110  hover:bg-green-600 hover:text-white font-semibold'>
              Shop Now
            </button>
          </NavLink>
        </div>)
     }

    </div>






  )
};

export default Cart;
